import ChessPiece, { PieceColor, PieceType } from "./ChessPiece"
import Move from "./Move";
import Pos from "./Pos";

export default class ChessBoard{
  static STARTING_FEN = "rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR"
  static PIECE_ORDER: PieceType[] = ["rook", "knight", "bishop", "queen", "king", "bishop", "knight", "rook"]

  whitePieces: Map<string, ChessPiece> = new Map();
  blackPieces: Map<string, ChessPiece> = new Map();

  enPassantPosition: Pos | undefined = undefined;
  whiteToMove = true
  moves = 0

  textureDict: {[key: string]: HTMLImageElement} = {}
  
  canMoveTo(x: number, y: number, x1: number, y1: number){
    let chessPiece = this.getPiece(x, y);
    if (chessPiece){
      let moves = chessPiece.getMoves(this);
    
      if (moves){
        for(let pos of moves){
          if(pos.x == x1 && pos.y == y1){
            return true;
          }
        }
      }
    }
    return false;
  }

  sim(color: PieceColor, depth: number, currentDepth = 0): number{
    let moves = 0;

    if (currentDepth == depth){
      return moves;
    }

    currentDepth ++;

    for (let piece of [...this.getPiecesOfColor(color).values()]){
      for (let move of piece.getMoves(this)){
        let prevX = piece.pos.x;
        let prevY = piece.pos.y;

        let pieceAtPos = this.getPiece(move.x, move.y);
        this.movePiece(piece, move.x, move.y)
        //Start

        if (currentDepth == depth){
          moves += 1
        }else{
          moves += this.sim(this.getOpposingColor(color), depth, currentDepth)
        }

        //End
        this.movePiece(piece, prevX, prevY)
        if (pieceAtPos)
          this.setPiece(new ChessPiece(pieceAtPos.type, pieceAtPos.color), move.x, move.y)
      }
    }
    return moves;
  }

  move(chessPiecePos: Pos, newPos: Pos, force = false){
    let chessPiece = this.getPiece(chessPiecePos.x, chessPiecePos.y);
    if (chessPiece){
      let moves = chessPiece.getMoves(this);
    
      let move;
      if (moves){
        for(let pos of moves){
          if(newPos.equals(pos.x, pos.y)){
            move = pos;
            break;
          }
        }
      }

      if (move || force){
        // Actually make the move
        this.movePiece(chessPiece, newPos.x, newPos.y)
        this.enPassantPosition = undefined;

        // Execute any aftermath (Pawn promotion, castling, en passant)
        if (!force && move instanceof Move){
          move.onPieceMove();
        }
      
        // Did this end the game?
        let opposingKing = this.getKing(this.getOpposingColor(chessPiece.color))!;


        let enemyKingInCheck = false;
        for (let piece of this.getPiecesOfColor(chessPiece.color).values()){
          if (enemyKingInCheck)
            break;

          for (let move of piece.getMoves(this, false)){
            if (move.equalsPos(opposingKing.pos)){
              enemyKingInCheck = true;
              break;
            }
          }
        }


        let enemyTeamHasAnyMoves = false;
        // Copy list so the 'simulations' don't interfer with the list order
        for (let piece of [...this.getPiecesOfColor(this.getOpposingColor(chessPiece.color)).values()]){
          let moves = piece.getMoves(this).length;

          if (moves != 0){
            enemyTeamHasAnyMoves = true;
            break;
          }
        }

        let isCheckmate = !enemyTeamHasAnyMoves && enemyKingInCheck;
        let isStalemate = !enemyTeamHasAnyMoves && !enemyKingInCheck;

        this.whiteToMove = !this.whiteToMove

        if (isCheckmate){
          console.log(`Checkmate. ${chessPiece.color} has won!`)
          return "checkmate";
        }else if(isStalemate){
          console.log(`Stalemate. The game is drawn`)
          return "stalemate"
        }
        
        return enemyKingInCheck ? "check" : "normal";
      }
    }
    return "invalid";
  }


  sameColorAs(chessPiece1: ChessPiece | undefined, chessPiece2: ChessPiece| undefined){
    if(chessPiece1 == undefined || chessPiece2 == undefined){
      return 'undefined';
    }

    return chessPiece1.color == chessPiece2.color;
  }
  
 
  init(fenString = ChessBoard.STARTING_FEN){
    this.whitePieces = new Map();
    this.blackPieces = new Map();

    this.whiteToMove = true;
    this.moves = 0;

    this.parseFen(fenString);
  }

  parseFen(fenString: string){
    let currentX = 0, currentY = 0
    for(let i = 0; i < fenString.length; i++){
      let char = fenString.charAt(i);

      if (char == "/"){
        currentY += 1
        currentX = 0;

      }else if(char >= '0' && char <= '9'){
        currentX += parseInt(char)

      }else{

        let pieceType: PieceType = ChessPiece.getTypeByLetter(char);
        let pieceColor: PieceColor = char.toUpperCase() == char ? "white" : "black";

        this.setPiece(new ChessPiece(pieceType, pieceColor), currentX, currentY);
        currentX += 1;
      }
    }
  }

  getFen(): string{
    let currentBlanks = 0
    let fenString = "";
    for (let y = 0; y < 8; y++){
      for(let x = 0; x < 8; x++){
        let chessPiece = this.getPiece(x, y);
        if(chessPiece){
          if (currentBlanks != 0){
            fenString += currentBlanks;
            currentBlanks = 0
          }

          let letter = ChessPiece.getLetterByType(chessPiece.type);

          if (chessPiece.color == "white")
            letter = letter.toUpperCase();

          fenString += letter
        }else{
          currentBlanks ++;
        }
      }
      if (currentBlanks != 0){
        fenString += currentBlanks;
        currentBlanks = 0
      }
      if (y != 7)
        fenString += "/";
    }
    return fenString;
  }

  isDarkSquare(x: number, y: number): boolean{
    return y % 2 == 0 ? x % 2 != 0 : x % 2 == 0;
  }

  getLocator(x: number, y: number): string{
    return `${x},${y}`
  }

  getPiece(x: number, y: number): ChessPiece | undefined{
    if (this.isValidPosition(x, y)){
      let stringLocator = this.getLocator(x,y);
      return this.whitePieces.get(stringLocator) || this.blackPieces.get(stringLocator);
    }else{
      return undefined
    }
  }

  pieceExistsOn(x: number, y: number): boolean{
    return this.getPiece(x, y) != undefined;
  }

  setPiece(chessPiece: ChessPiece | undefined, x: number, y: number){
    let stringLocator = this.getLocator(x,y);

    if (!chessPiece){
      this.removePiece(x, y);
    }else{
      this.removePiece(x, y);
      if (chessPiece.color == "white"){
        this.whitePieces.set(stringLocator, chessPiece);
      }else{
        this.blackPieces.set(stringLocator, chessPiece);
      }
      chessPiece.pos.set(x, y);
    }
  }

  getKing(string: PieceColor){
    let pieces = this.getOppositeColoredPieces(string == "white" ? "black" : "white");

    for (let piece of pieces.values()){
      //console.log(`Grabbing king: ${piece}`)
      if (piece.type == "king"){
        return piece;
      }
    }
  }

  getPiecesOfColor(color: PieceColor){
    return color == "white" ? this.whitePieces : this.blackPieces;
  }

  getOpposingColor(color: PieceColor){
    return color == "white" ? "black" : "white";
  }

  getOppositeColoredPieces(string: PieceColor){
    if (string == "white"){
      return this.blackPieces
    }else{
      return this.whitePieces;
    }
  }

  removePiece(x: number, y: number){
    let stringLocator = this.getLocator(x,y);

    if (this.whitePieces.get(stringLocator)){
      this.whitePieces.delete(stringLocator);

    }else if (this.blackPieces.get(stringLocator)){
      this.blackPieces.delete(stringLocator);
    }
  }

  movePiece(chessPiece: ChessPiece, newX: number, newY: number){
    if (this.getPiece(chessPiece.pos.x, chessPiece.pos.y)){
      this.setPiece(undefined, chessPiece.pos.x, chessPiece.pos.y);
      this.setPiece(chessPiece, newX, newY);

      chessPiece.move(newX, newY);
    }
  }


  

  isValidPosition(x: number, y: number): boolean{
    return x >= 0 && y >= 0 && x < 8 && y < 8
  }
  
  canPieceBePlayed(chessPiece: ChessPiece): boolean{
    return (this.whiteToMove && chessPiece.color == "white") || (!this.whiteToMove && chessPiece.color == "black")
  }
}

import ChessBoard from "./ChessBoard"
import AxisPiece from "./AxisPiece"
import Pos from "./Pos"
import TextureHandler from "./TextureHandler";
import Move from "./Move";
import Utils from "./Utils";

export type PieceType = 'pawn' | 'bishop' | 'knight' | 'rook' | 'queen' | 'king';
export type PieceColor = 'white' | 'black';

export default class ChessPiece{
  type: PieceType
  color: PieceColor;
  texture: HTMLImageElement;

  pos: Pos = new Pos(0,0);
  moves: number;
  
  constructor(type: PieceType, color: PieceColor){
    this.type = type
    this.color = color
    this.texture = TextureHandler.textureDict[color.toUpperCase() + "_" + type.toUpperCase()];
    
    this.moves = 0;
  }

  static matches(chessPiece: ChessPiece | undefined, color?: PieceColor, type?: PieceType, moves?: number){
    if (!chessPiece)
      return false;

    let matches = true;
    if (color) matches = matches && chessPiece.color == color
    if (type) matches = matches && chessPiece.type == type
    if (moves) matches = matches && chessPiece.moves == moves

    return matches;
  }


  static getTypeByLetter(letter: string): PieceType{
    switch(letter.toLowerCase()){
      case 'p': return "pawn";
      case 'n': return "knight";
      case 'b': return "bishop";
      case 'r': return "rook";
      case 'q': return "queen";
      case 'k': return "king";
      default: throw "Invalid char!";
    }
  }

  static getLetterByType(pieceType: PieceType): string{
    switch(pieceType){
      case 'knight': return "n";
      default: return pieceType.toLowerCase().charAt(0);
    }
  }


  toString(){
    return `ChessPiece {${this.color} ${this.type}}`
  }

  move(x: number, y: number){
    this.pos.set(x, y);
    this.moves += 1;
  }
  

  getMoves(board: ChessBoard, checkForChecks = true): Pos[]{
    let moves: Pos[] = [];
    let x = this.pos.x, y = this.pos.y;
    
    if (this.type == "pawn"){
      let mul = this.color == "white" ? -1 : 1;

      let pawnCanPromote = (this.color == "white" && y == 1) || (this.color == "black" && y == 6);
      if (board.getPiece(x, y + mul) == undefined){
        let move = new Move(x, y + mul);

        if (pawnCanPromote){
          move.addAfterMath(() => {
            board.setPiece(new ChessPiece("queen", this.color), x, y + mul)
          });
        }
        moves.push(move);

        let pawnCanMoveForwardTwoSpaces = (this.color == "white" && y == 6) || (this.color == "black" && y == 1)
        if(pawnCanMoveForwardTwoSpaces && board.getPiece(x, y + mul * 2) == undefined){
          moves.push(new Move(x, y + mul * 2));
        }
      }
      
      let xRight = x + 1;
      if (xRight < 8){
        let opponentPieceExists = board.sameColorAs(this, board.getPiece(xRight, y + mul)) === false
        if (opponentPieceExists){
          let rightPawnPos = new Move(xRight, y + mul);

          if (pawnCanPromote){
            rightPawnPos.addAfterMath(() => {
              board.setPiece(new ChessPiece("queen", this.color), xRight, y + mul)
            });
          }
          moves.push(rightPawnPos);
        }else if(board.enPassantPosition && board.enPassantPosition.equals(xRight, y) && board.sameColorAs(this, board.getPiece(xRight, y)) === false){
          moves.push(new Pos(xRight, y + mul));
        }
      }

      let xLeft = x - 1;
      if(xLeft >= 0){
        let opponentPieceExists = board.sameColorAs(this, board.getPiece(xLeft, y + mul)) === false
        if (opponentPieceExists){
          let leftPawnPos = new Move(xLeft, y + mul);

          if (pawnCanPromote){
            leftPawnPos.addAfterMath(() => {
              board.setPiece(new ChessPiece("queen", this.color), xLeft, y + mul)
            });
          }
          moves.push(leftPawnPos);
        }else if(board.enPassantPosition && board.enPassantPosition.equals(xLeft, y) && board.sameColorAs(this, board.getPiece(xLeft, y)) === false){
          moves.push(new Pos(xLeft, y + mul));
        }
      }
    }else if (this.type == "knight"){
      moves = ChessPiece.fromOffsets(this, board, [new Pos(1, 2), new Pos(-1, 2),
                     new Pos(1, -2), new Pos(-1, -2),
                     new Pos(2, 1), new Pos(-2, 1),
                     new Pos(2, -1), new Pos(-2, -1)]);

    }else if(this.type == "bishop"){
      moves = AxisPiece.getMoves(this, board, ["diagonal_left", "diagonal_right"]);
      
    }else if(this.type == "queen"){
      moves = AxisPiece.getMoves(this, board, AxisPiece.getAllAxes());
      
    }else if(this.type == "rook"){
      moves = AxisPiece.getMoves(this, board, ["vertical", "horizontal"]);
      
    }else if(this.type == "king"){
      moves = ChessPiece.fromOffsets(this, board, 
                    [new Pos(0, 1), new Pos(1, 0),
                     new Pos(1, 1), new Pos(-1, -1),
                     new Pos(-1, 1), new Pos(1, -1),
                     new Pos(0, -1), new Pos(-1, 0)]);


      if (this.moves != 0){
        let backrankYPosition = this.color == "white" ? 7 : 0
 
        let leftRook = board.getPiece(7, backrankYPosition);
        let rightRook = board.getPiece(0, backrankYPosition);
  
        let canLongCastle = ChessPiece.matches(leftRook, this.color, "rook", 0);
        if (leftRook && canLongCastle && leftRook.moves == 0){
          if (!board.pieceExistsOn(x + 1, backrankYPosition) && !board.pieceExistsOn(x + 2, backrankYPosition)){
            moves.push(new Move(x + 2, backrankYPosition).addAfterMath(() => board.movePiece(leftRook!, x + 1, y)));
          }
        }
      
        let canShortCastle = ChessPiece.matches(rightRook, this.color, "rook", 0);
        if (rightRook && canShortCastle && rightRook.moves == 0){
          if (!board.pieceExistsOn(x - 1, backrankYPosition) && !board.pieceExistsOn(x - 2, backrankYPosition) && !board.pieceExistsOn(x - 3, backrankYPosition)){
            moves.push(new Move(x - 2, backrankYPosition).addAfterMath(() => board.movePiece(rightRook!, x - 1, y)));
          }
        }
      }
    }

    // Will my move put my king in check or make it so my king is still in check?
    if (checkForChecks){
      let king = board.getKing(this.color);

      if (!king){
        console.log("Warning: King not present on board!");

      }else{
        let prevX = this.pos.x;
        let prevY = this.pos.y;

        let legalMoves = [];
        for (let move of moves){
          let pieceAtPos = board.getPiece(move.x, move.y);
          board.movePiece(this, move.x, move.y)
          
          let legalMove = true;
          for (let piece of board.getOppositeColoredPieces(this.color).values()){
            if (!legalMove)
              break;

            for(let move of piece.getMoves(board, false)){
              if (move.x == king.pos.x && move.y == king.pos.y){
                legalMove = false;
                break;
              }
            }
          }
    
          if (legalMove)
            legalMoves.push(move)
    
          //End simulation
          board.movePiece(this, prevX, prevY)
          if (pieceAtPos)
            board.setPiece(new ChessPiece(pieceAtPos.type, pieceAtPos.color), move.x, move.y)
        }
        moves = legalMoves
      }
      
    }

    return moves;
  }

  static fromOffsets(chessPiece: ChessPiece, board: ChessBoard, offsets: Pos[]): Pos[]{
    let moves: Pos[] = [];

    let initPos = new Pos(chessPiece.pos.x, chessPiece.pos.y);
    for (let offset of offsets){
      let realPos = initPos.plus(offset.x, offset.y);

      if (Utils.isValidPosition(realPos.x, realPos.y)){
        let piece = board.getPiece(realPos.x, realPos.y)
        if (!ChessPiece.matches(piece, chessPiece.color)){
          moves.push(realPos)
        }
      }
    }
    return moves;
  }
}

import ChessBoard from "./ChessBoard"
import ChessPiece from "./ChessPiece"
import Pos from "./Pos"
import Utils from "./Utils";

type Axis = "vertical" | "horizontal" | "diagonal_left" | "diagonal_right";
export default class AxisPiece{

  static getAllAxes(): Axis[]{
    return ["vertical", "horizontal", "diagonal_left", "diagonal_right"]
  }

  static getIncrementByAxis(axis: Axis): Pos{
    let incrementX = 0, incrementY = 0;

    switch (axis){
      case "vertical":
        incrementY = 1
        break;
      case "horizontal":
        incrementX = 1;
        break
      case "diagonal_left":
        incrementX = 1;
        incrementY = 1;
        break
      case "diagonal_right":
        incrementX = -1;
        incrementY = 1;
        break
    }
    return new Pos(incrementX, incrementY);
  }
  
  static getMoves(chessPiece: ChessPiece, board: ChessBoard, axes: Axis[]): Pos[]{
    let moves: Pos[] = [];
    let x = chessPiece.pos.x, y = chessPiece.pos.y;
    
    for (let axis of axes){
      let increment = this.getIncrementByAxis(axis);

      let movePositive = true, moveNegative = true;
      for (let i = 1; i < 8; i++){
        if(movePositive){
          let currentX = x + (i * increment.x);
          let currentY = y + (i * increment.y);
  
          if (!this.addMove(moves, chessPiece, board, currentX, currentY)){
            movePositive = false;
          }
        }

        if(moveNegative){
          let currentX = x + (i * -increment.x);
          let currentY = y + (i * -increment.y);
  
          if (!this.addMove(moves, chessPiece, board, currentX, currentY)){
            moveNegative = false;
          }
        }

        if (!movePositive && !moveNegative){
          break
        }
      }

      
    }
    return moves
  } 

  //Returns boolean: should continue searching for more moves
  static addMove(moves: Pos[], chessPiece: ChessPiece, board: ChessBoard, x: number, y: number): boolean{
    if (Utils.isValidPosition(x, y)){
      let sameColor = board.sameColorAs(chessPiece, board.getPiece(x, y));

      if (sameColor === true){
        return false;
      }else if (sameColor === false){
        moves.push(new Pos(x, y));
        return false;
      }else{
        moves.push(new Pos(x, y));
        return true;
      }
    }
    return false;
  }
  
}

export default class Pos{
  x: number;
  y: number;
    
  constructor(x: number, y: number){
    this.x = x;
    this.y = y;
  }
  
  static copyOf(pos: Pos){
    return new Pos(pos.x, pos.y);
  }
  
  set(x: number, y: number){
    this.x = x;
    this.y = y;
  }
  
  equalsPos(pos: Pos | undefined): boolean{
    return pos != undefined && this.equals(pos.x, pos.y);
  }
  
  equals(x: number, y: number): boolean{
    return this.x == x && this.y == y;
  }
  
  plus(x: number, y: number){
    return new Pos(this.x + x, this.y + y);
  }
}

import Pos from "./Pos"

export default class Utils{
  static clear(canvas: HTMLCanvasElement, context: CanvasRenderingContext2D){
    Utils.fillRect(context, 0, 0, canvas.width, canvas.height, "white");
  }
  
  static fillRect(context: CanvasRenderingContext2D, x: number, y: number, width: number, height: number, color: string){
    context.fillStyle = color;
    context.fillRect(x, y, width, height);
  }
  
  static circle(context: CanvasRenderingContext2D, x: number, y: number, radius: number, color: string){
    context.strokeStyle = color;
    context.beginPath();
    context.arc(x, y, radius, 0, 2 * Math.PI);
    context.stroke();
  }

  static getCursorPosition(canvas: HTMLCanvasElement, event: any): Pos {
    const rect = canvas.getBoundingClientRect()
    return new Pos(event.clientX - rect.left, event.clientY - rect.top)
  }

  static isValidPosition(x: number, y: number): boolean{
    return x >= 0 && y >= 0 && x < 8 && y < 8
  }

  static toChessPos(x: number, y: number){
    if (Utils.isValidPosition(x, y)){
      return String.fromCharCode("A".charCodeAt(0) + x) + (8 - y);
    }else{
      return `Invalid pos: ${x},${y}`;
    }
  }

  static fromChessPos(chessPos: string): Pos{
    let x =   chessPos.charCodeAt(0) - "A".charCodeAt(0);
    let y = 8 - (parseInt(chessPos.charAt(1)));

    return new Pos(x, y);
  }
}

export default class TextureHandler{
  static SHOULD_LOAD_FROM_GITHUB = true;

  static textureDict: {[key: string]: HTMLImageElement} = {};

  static async loadAll(){
    const textureList: [string, HTMLImageElement][] = await Promise.all([
      TextureHandler.load("pawn", "white"), 
      TextureHandler.load("bishop", "white"),
      TextureHandler.load("knight", "white"),
      TextureHandler.load("rook", "white"),
      TextureHandler.load("queen", "white"),
      TextureHandler.load("king", "white"),
      
      TextureHandler.load("pawn", "black"), 
      TextureHandler.load("bishop", "black"),
      TextureHandler.load("knight", "black"),
      TextureHandler.load("rook", "black"),
      TextureHandler.load("queen", "black"),
      TextureHandler.load("king", "black")]);

      console.log("Loaded all chess pieces")

      for(let textureComponent of textureList){
        TextureHandler.textureDict[textureComponent[0]] = textureComponent[1];
      }
    }

  static async load(type: string, color: string): Promise<[string, HTMLImageElement]>{
      let img: HTMLImageElement = await new Promise((resolve) => {
        let img = new Image();
    
        let colorChar = color.charAt(0);
        let typeChar = type == "knight" ? "N" : type.toUpperCase().charAt(0);
        
        if (TextureHandler.SHOULD_LOAD_FROM_GITHUB){
          img.src = `https://raw.githubusercontent.com/lichess-org/lila/fcfe042d774ece49e1a5d124d81af0e7996f365a/public/piece/kosal/${colorChar}${typeChar}.svg`;
        }else{
          img.src = `./assets/${colorChar}${typeChar}.png`;
        }
    
        img.onload = function(){
          console.log(`Loaded ${color} ${type}`)
          resolve(img);
        }

        img.onerror = function(){
          console.log("error: can't load lmao")
        }

        img.onabort = function(){
          console.log("saddo")
        }

        img.onclose = function(){
          console.log("close :(")
        }
      });
      return [color.toUpperCase() + "_" + type.toUpperCase(), img];
  }
}

import ChessBoard from "./ChessBoard";
import Pos from "./Pos";

export default class Move extends Pos{
    onPieceMove: Function;

    constructor(x: number, y: number){
        super(x, y);
        this.onPieceMove = () => {};
    }

    addAfterMath(onPieceMove: Function){
        this.onPieceMove = () => {
            onPieceMove();
        }
        return this;
    }
}
